var time = DateAndTime.everyDayAt.CheckTime;
if (time.includes("AM") && time.getHours() < 9) {
  Datadotworld.sync.skip("Outside of active hours")
} else if (time.includes("PM") && time.getHours() > 5) {
  Datadotworld.sync.skip("Outside of active hours")
} else {
  var datasetKey = "dataset_key"
  Datadotworld.sync(datasetKey)
}
