var checkTime = DateAndTime.everyMonthOnThe.CheckTime;
var [dateStr, timeStr] = checkTime.split(' at ');

// Split date part into month name, day, year
var parts = dateStr.split(' ');
var monthName = parts[0];
var dayWithComma = parts[1];
var day = parseInt(dayWithComma.replace(',', ''));
var currentYear = parseInt(parts[2]);

// Map month name to index (January is 0)
var monthIndex;
switch(monthName) {
    case 'January': monthIndex=0; break;
    case 'February': monthIndex=1; break;
    case 'March': monthIndex=2; break;
    case 'April': monthIndex=3; break;
    case 'May': monthIndex=4; break;
    case 'June': monthIndex=5; break;
    case 'July': monthIndex=6; break;
    case 'August': monthIndex=7; break;
    case 'September': monthIndex=8; break;
    case 'October': monthIndex=9; break;
    case 'November': monthIndex=10; break;
    case 'December': monthIndex=11; break;
}

// Calculate last day of the current month/year
var lastDay = new Date(currentYear, monthIndex + 1, 0).getDate();

if (day !== lastDay) {
    IfNotifications.sendNotification.skip("Not the last day of the month.");
}

// Process time part: e.g., "8:00 AM"
var [timePart, period] = timeStr.split(' ');
var [hoursStr, minutesStr] = timePart.split(':');
var hours = parseInt(hoursStr);
var minutes = parseInt(minutesStr);

if (period === 'PM' && hours < 12) {
    hours += 12;
} else if (period === 'AM' && hours === 12) {
    hours = 0; // Convert noon in AM to midnight
}

if (hours !== 8 || minutes !== 0) {
    IfNotifications.sendNotification.skip("Time is not exactly 8:00 AM.");
}

IfNotifications.sendNotification.setMessage("Reminder: Rent is due today, please pay up to avoid late fees.");

